mapper.select:
SELECT
t1.merchandise_code merchandiseCode, t1.merchandise_name merchandiseName, t2.supplier_code supplierCode, t2.supplier_name supplierName, t1.centre_type_code centreTypeCode, t3.centre_type_name centreTypeName, t1.small_type_code smallTypeCode, t4.small_type_name smallTypeName, t1.detail_type_code detailTypeCode, t5.detail_type_name detailTypeName, k2.fine_type_code fineTypeCode, k3.fine_type_name fineTypeName, t6.dx_role_code dxRoleCode, t7.role_name dxRoleName, t6.dl_role_code dlRoleCode, t8.role_name dlRoleName
count(1) AS record_count
FROM merchandise t1 INNER JOIN supplier t2 ON t1.supplier_code = t2.supplier_code LEFT JOIN merchandise_centre_type t3 ON t1.centre_type_code = t3.centre_type_code LEFT JOIN merchandise_small_type t4 ON t1.small_type_code = t4.small_type_code LEFT JOIN merchandise_detail_type t5 ON t1.detail_type_code = t5.detail_type_code LEFT JOIN merchandise_role t6 ON t1.merchandise_code = t6.merchandise_code LEFT JOIN merchandise_dx_role t7 ON t6.dx_role_code = t7.role_code LEFT JOIN merchandise_dl_role t8 ON t6.dl_role_code = t8.role_code LEFT JOIN intention_supplier_merchandise k1 ON t1.merchandise_code = k1.merchandise_code AND t1.supplier_code = k1.supplier_code LEFT JOIN merchandise_intention k2 ON k1.intention_code = k2.intention_code LEFT JOIN merchandise_fine_type k3 ON k2.fine_type_code = k3.fine_type_code
1=1
AND UPPER(t1.merchandise_code) LIKE UPPER('%'||#{merchandiseCode}||'%')
AND t1.merchandise_code IN (${merchandiseCodes})
AND UPPER(t1.merchandise_name) LIKE UPPER('%'||#{merchandiseName}||'%')
AND UPPER(t1.supplier_code) LIKE UPPER('%'||#{supplierCode}||'%')
AND UPPER(t2.supplier_name) LIKE UPPER('%'||#{supplierName}||'%')
AND t1.centre_type_code = #{centreTypeCode}
AND t1.small_type_code = #{smallTypeCode}
AND t1.detail_type_code =#{detailTypeCode}
AND k3.fine_type_code =#{fineTypeCode}
AND t6.dx_role_code = #{dxRoleCode}
AND t6.dl_role_code =#{dlRoleCode}
AND t1.net_weight =1
AND t1.net_weight !=1
AND t1.purchase_departments =#{purchaseDepartments}
ORDER BY ${app_orderby}
ORDER BY t1.merchandise_code DESC, t1.supplier_code DESC
